function PeliculaDetail(props) {
  const id = props.match.params.id
  const peli = props.peliculas.find(p => p.id = id)
  return (
    <>
        <div>
            <h1>{peli.titulo}</h1>
            <p>{peli.descripcion}</p>
        </div>
    </>
  )
}

export default PeliculaDetail
